import React, { useState, useMemo } from 'react';

import Pagination from '../components/Pagination';
let PageSize = 20;


const Table = (props) => {

  const { data, columns } = props;
  const [currentPage, setCurrentPage] = useState(1);
  const [dataPerPage, setDataPerPage] = useState(10);
  const [paginatedDAnimation, setPaginatedData] = useState(data.SVGElementInstance)

  const paginate = (pageNumbers) => {
    setCurrentPage(pageNumbers)
  }
  return (
    <div>
      <table>
        <tr>
          {
            columns.map(ele => (
              <th className={ele.headerClassName}>{ele.Header}</th>
            ))
          }
        </tr>
        {
          data.map((ele) => {
            return (
              <tr>
                {
                  columns.map(colum => {
                    const { Cell, width, height } = colum;
                    return <td style={{ width, height }}>{
                      Cell ? <Cell {...ele} /> : colum.accessor(ele)
                    }</td>
                  })
                }
              </tr>
            );
          })
        }
      </table>
      <Pagination
        dataPerPage={dataPerPage}
        currentPage={currentPage}
        totalCount={data.length}
        pageSize={PageSize}
        paginate={paginate}
      />
    </div>
  )
};

export default Table;
