import React, { Fragment, useState } from "react";
import { connect } from "react-redux";
import styles from './userList.module.css';

import {
  editUser as editUserAction, deleteUser as deleteUserAction
} from '../redux/actions';


const UserList = (props) => {
  const { allUser, deleteUser, history } = props;
  const users = Object.values(allUser);

  const editUserHandler = (ele) => {

    const { email, phone, name } = ele;
    history.push('/create-user', {
      userEmail: email,
      userName: name,
      userPhone: phone
    });
  };

  return (
    <div className={styles.mainContainer}>
      <div className={styles.subContainer}>
        <button className={styles.createBtn} onClick={() => history.push('/create-user')} >Create New User </button>
      </div>
      <div>
        <div className={styles.tableContainer}>
          <table>
            <tr>
              <th>name</th>
              <th>email</th>
              <th>phone</th>
            </tr>
            {
              users.map((ele) => {
                const { email } = ele;
                return (
                  <tr>
                    <td>{ele.name}</td>
                    <td>{ele.email}</td>
                    <td>{ele.phone}</td>
                    <div className={styles.btnContainer}>
                      <div>
                        <button className={styles.button} onClick={() => deleteUser(email)} >Delete </button>
                      </div>
                      <div >
                        <button className={styles.button} onClick={() => editUserHandler(ele)} >Edit </button>
                      </div>
                    </div>
                  </tr>
                )
              })
            }
          </table>
        </div>
        );
          })}
      </div>
    </div>
  )
}

const mapStateToProps = (state) => {
  console.log(state, "=state")
  const { allUser } = state;
  return { allUser }
}

const mapDispatchToProps = (dispatch) => ({
  deleteUser: data => dispatch(deleteUserAction(data)),
})

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(UserList);
