import React, { Fragment, useState } from "react";
import { connect } from "react-redux";
import Table from './Table';


import {
  editUser as editUserAction, deleteUser as deleteUserAction
} from '../redux/actions';

import styles from './userList.module.css';


const UserList = (props) => {

  const { allUser, deleteUser, history } = props;
  const users = Object.values(allUser);

  const editUserHandler = (ele) => {
    const { email, phone, name } = ele;
    history.push('/create-user', {
      userEmail: email,
      userName: name,
      userPhone: phone
    });
  };

  const DeleteButton = (email) => {
    return (
      <button
        className={styles.button}
        onClick={() => deleteUser(email)}
      >
        Delete
      </button>
    );
  }

  const EditButton = ({ email, phone, name }) => {
    return (
      <button
        className={styles.button}
        onClick={
          () => history.push('/edit-user', {
            userName: name, userPhone: phone, userEmail: email,
          })
        }
      >
        Edit
      </button >
    );
  }
  allUsers = [
    {

    }
  ]

  const columns = [
    {
      id: 'name',
      Header: 'Name',
      accessor: 'name',
    },
    {
      id: 'email',
      Header: 'Email',
      accessor: 'email',
    },
    {
      id: 'phone',
      Header: 'Phone',
      accessor: 'phone',
    },
    {
      id: 'delete',
      Header: '',
      accessor: (row) => row.email,
      Cell: DeleteButton,
    },
    {
      id: 'edit',
      Header: '',
      accessor: (row) => row,
      Cell: EditButton,
    }
  ]

  return (
    <div className={styles.mainContainer}>
      <div className={styles.subContainer}>
        <button
          className={styles.createBtn}
          onClick={() => history.push('/create-user')}
        >Create New User </button>
      </div>
      <div>
        <div className={styles.tableContainer}>
          <Table columns={columns} data={users} />
        </div>
      </div>
    </div>
  )
}

const mapStateToProps = (state) => {
  const { allUser } = state;
  return { allUser }
}

const mapDispatchToProps = (dispatch) => ({
  deleteUser: data => dispatch(deleteUserAction(data)),
})

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(UserList);
