import React, { Fragment, useState } from "react";
import { connect } from "react-redux";
import styles from './createuser.module.css';
// import { notification } from "antd";
import {
  editUser as editUserAction, deleteUser as deleteUserAction
} from '../redux/actions';

const columns = [
  {
    id: 'id',
    Header: 'ID',
    accessor: 'id',
    className: styles.alignText,
    width: 100,
  },
  {
    id: 'login',
    Header: 'Login',
    accesssor: 'login',
    className: styles.alignText,
    width: 100,
    Cell: props => <span>{props.original.login}</span>
  },
  {
    id: 'type',
    Header: 'type',
    accessor: 'type',
    className: styles.alignText,
    width: 100,
  }
]
const UserList = (props) => {
  const { allUser, deleteUser, history } = props;
  const users = Object.values(allUser);

  const editUserHandler = (ele) => {
    const { email, phone, name } = ele;
    history.push('/create-user', {
      userEmail: email,
      userName: name,
      userPhone: phone
    });
  };

  return (
    <div>
      <div>
      <button onClick={() => history.push('/create-user')} >Create New User </button>
      </div>
      <div>
        {
          users.map((ele) => {
            const { email } = ele;
            return (
              <div>
                <div>{ele.name}</div>
                <div>{ele.email}</div>
                <div>{ele.phone}</div>
                <div><button onClick={() => deleteUser(email)} >Delete </button></div>
                <div><button onClick={() => editUserHandler(ele)} >Edit </button></div>
              </div>
            );
          })}
      </div>
    </div>
  )
}

const mapStateToProps = (state) => {
  console.log(state, "=state")
  const { allUser } = state;
  return { allUser }
}

const mapDispatchToProps = (dispatch) => ({
  deleteUser: data => dispatch(deleteUserAction(data)),
})

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(UserList);
