import React, { Fragment, useState } from "react";
import { connect } from "react-redux";
import styles from './createuser.module.css';
// import { notification } from "antd";
import {
  editUser as editUserAction, deleteUser as deleteUserAction
} from '../redux/actions';


const UserList = (props) => {
  const { allUser, deleteUser, history } = props;
  const users = Object.values(allUser);

  const editUserHandler = (ele) => {
    const { email, phone, name } = ele;
    history.push('/create-user', {
      userEmail: email,
      userName: name,
      userPhone: phone
    });
  };

  return (
    <div>
      <div>
      <button onClick={() => editUserHandler(ele)} >Edit </button>
      </div>
      <div>
        {
          users.map((ele) => {
            const { email } = ele;
            return (
              <div>
                <div>{ele.name}</div>
                <div>{ele.email}</div>
                <div>{ele.phone}</div>
                <div><button onClick={() => deleteUser(email)} >Delete </button></div>
                <div><button onClick={() => editUserHandler(ele)} >Edit </button></div>
              </div>
            );
          })}
      </div>
    </div>
  )
}

const mapStateToProps = (state) => {
  console.log(state, "=state")
  const { allUser } = state;
  return { allUser }
}

const mapDispatchToProps = (dispatch) => ({
  deleteUser: data => dispatch(deleteUserAction(data)),
})

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(UserList);
