import React from 'react'
import style from './pagination.module.css';


const Pagination = (props) => {
  const {
    currentPage,
    totalCount,
    pageSize,
    onPageChange,
  } = props;

  const pageNumbers = [];
  const value = Math.ceil(totalCount / pageSize)
  for (let i = 1; i <= value; i++) {
    pageNumbers.push(i);
  }

  return (
    <div className={style.pagination}>
      <button>&raquo;</button>
      <div className={style.ulList}>
        <ul  >
          {pageNumbers.map((number, i) => {
            return (
              <li key={number}>
                <a onclick={() => onPageChange(number)} href="!#">
                  {number}
                </a>
              </li>
            )
          })}
        </ul>
      </div>
      <button>&raquo;</button>
    </div>
  );
}

export default Pagination
