import React, { Fragment, useState } from "react";
import { connect } from "react-redux";
import styles from './createuser.module.css';
// import { notification } from "antd";
import { createUser as createUserAction, editUser as editUserAction } from '../redux/actions';

const CreateAndEditUser = (props) => {
  const { createUser, location, editUser } = props;
  const { state: { userEmail }} = location;

  const isEditableMode = userEmail ? true : false;

  const [email, setEmail] = useState('');
  const [userName, setUserName] = useState('');
  const [phone, setPhone] = useState();


  const submitformHandler = (e) => {
    debugger;
    e.preventDefault();
    const data = {
      email,
      userName,
      phone
    }

    if(isEditableMode) {
      editUser.userEmail = userEmail;
      editUser(data)
    }

    createUser(data);
    // notification({
    //   description:
    //     'user created successfully',
    // });
    alert(" user created");
  }

  return (

    <Fragment>
      <form action="" className={styles.formContainer}>
        <div className={styles.container}>
          <div className={styles.userContainer}>
            <label className={styles.label}>UserName</label>
            <input
              className={styles.inputContainer}
              type="text" value={userName}
              onChange={(e) => setUserName(e.target.value)}
            />
          </div>

          <div className={styles.userContainer}>
            <label className={styles.label}>Email</label>
            <input
              className={styles.inputContainer} type="text"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>

          <div className={styles.userContainer}>
            <label className={styles.label}>Phone</label>
            <input
              className={styles.inputContainer}
              type="text" value={phone}
              onChange={(e) => setPhone(e.target.value)}
            />
          </div>

          <div className={styles.btnContainer}>
            <button
              className={styles.button}
              type="submit" onClick={submitformHandler}
            >
              Create User
            </button>
            {/* <button
              className={styles.button}
              type="submit" onSubmit={submitformHandler}

            >
              Edit User
            </button> */}
          </div>
        </div>
      </form>
    </Fragment >
  )
}
const mapDispatchToProps = (dispatch) => ({
  createUser: data => dispatch(createUserAction(data)),
  editUser: data => dispatch(editUserAction(data)),
})

export default connect(
  null,
  mapDispatchToProps
)(CreateAndEditUser);
