import { createStore } from "redux";
import rootReducer from "./reducers";

function makeStore(reducers, history, middlewares = []) {
  const { rootReducer, defaultReducers } = reducers;
  const middlewareList = [, batch, routerMiddleware(history), ...middlewares];
  const middlewareEnhancer = applyMiddleware(...middlewareList);

  const enhancers = [middlewareEnhancer];

  // If Redux DevTools Extension is installed use it, otherwise use Redux compose
  const composeEnhancers = process.env.NODE_ENV !== 'production'
  && typeof window === 'object'
  && window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__
    ? window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__
    : compose;

  const store = createStore(
    batching(rootReducer),
    composeEnhancers(...enhancers)
  );

  store.asyncReducers = {};
  store.defaultReducers = defaultReducers;

  return store;
}

export default createStore(rootReducer);
