import React, { Fragment, useState } from "react";
import { connect } from "react-redux";
import ReactTable from 'react-table';


import {
  editUser as editUserAction, deleteUser as deleteUserAction
} from '../redux/actions';

import styles from './userList.module.css';


const UserList = (props) => {

  const { allUser, deleteUser, history } = props;
  const users = Object.values(allUser);

  const editUserHandler = (ele) => {
    const { email, phone, name } = ele;
    history.push('/create-user', {
      userEmail: email,
      userName: name,
      userPhone: phone
    });
  };

  const columns = [
    {
      id: 'name',
      Header: 'Name',
      minWidth: 80,
      accessor: getTableAccessor(_property('pace')),
      pivotAccessor: getPivotAccessor('pace'),
      Cell: TextRenderer,
      PivotCell: TextRenderer,
      visibleFor: VALID_FOR_ALL,
    },
  ]

  return (
    <div className={styles.mainContainer}>
      <div className={styles.subContainer}>
        <button
          className={styles.createBtn}
          onClick={() => history.push('/create-user')}
        >Create New User </button>
      </div>
      <div>
        <div className={styles.tableContainer}>
          <table>
            <tr>
              <th>name</th>
              <th>email</th>
              <th>phone</th>
            </tr>
            {
              users.map((ele) => {
                const { email } = ele;
                return (
                  <tr>
                    <td>{ele.name}</td>
                    <td>{ele.email}</td>
                    <td>{ele.phone}</td>
                    <td className={styles.btnContainer}>
                      <div>
                        <button
                          className={styles.button}
                          onClick={() => deleteUser(email)} >
                          Delete
                        </button>
                      </div>
                      <div >
                        <button
                          className={styles.button}
                          onClick={() => editUserHandler(ele)}
                        >Edit
                        </button>
                      </div>
                    </td>
                  </tr>
                )
              })
            }
          </table>
        </div>
      </div>
    </div>
  )
}

const mapStateToProps = (state) => {
  console.log(state, "=state")
  const { allUser } = state;
  return { allUser }
}

const mapDispatchToProps = (dispatch) => ({
  deleteUser: data => dispatch(deleteUserAction(data)),
})

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(UserList);
