      accessor: 'phone',
      className: styles.tableCellCenter,
      headerClassName: styles.tableHeaderCenter,
    },
    {
      id: 'delete',
      Header: '',
      Cell: DeleteButton,
      className: styles.tableCellCenter,
      headerClassName: styles.tableHeaderCenter,
      width: 120,
    },
    {
      id: 'edit',
      Header: '',
      Cell: EditButton,
      className: styles.tableCellCenter,
      headerClassName: styles.tableHeaderCenter,
      width: 120,
    }
  ]


  const EditButton = ({ email, phone, name  }) => {
    return (
      <button
        className={styles.button}
        onClick={
          () => history.push('/edit-user', {
            userName: name, userPhone: phone, userEmail: email,
